import './App.css';
import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faPizzaSlice } from '@fortawesome/free-solid-svg-icons'
import { faPlus } from '@fortawesome/free-solid-svg-icons'

function App(props) {
  const [open, setOpen] = useState(false);
  const [rightSide, setRightSide] = useState(false);
  const [dunderbaks, setDunderbaks] = useState([{
    restaurant: "Dunderbaks",
    city: "Tampa",
    state: "FL",
    rating: 5,
    description: "A German themed restaurant with amazing German food."
  }]);
  function AddItemOpen(x) {
    if (x === false) {
      document.getElementById("addItem_main").style.width = "400px";
      document.getElementById("addItem_main").style.height = "400px";
      document.getElementById("addItem_main").style.borderRadius = "20px 20px 75px 20px";
      document.getElementById("plusIcon").style.transform = "rotate(45deg) translate(-75%, -5%)";
      setOpen(true);
    } else {
      document.getElementById("addItem_main").style.width = "75px";
      document.getElementById("addItem_main").style.height = "75px";
      document.getElementById("addItem_main").style.borderRadius = "75px";
      document.getElementById("plusIcon").style.transform = "rotate(0deg) translate(-50%, -50%)";
      setOpen(false);
    }
  }
  function rightSideShow(x) {
    if (x === false) {
      document.getElementById("main").style.gridTemplateColumns = "60% auto";
    } else {
      document.getElementById("main").style.gridTemplateColumns = "100%";
    }
  }

  return (

    <div className="App">
      <header className="App-header">
        <div id="title">
          <FontAwesomeIcon
            className="pizzaIcon"
            icon={faPizzaSlice}
          />
          <h1>Eman's Food Reviews</h1>
        </div>
        <input
          id="search"
          type="text"
          name="search"
          placeholder="Lets Eat!"
          autocomplete="off"
        >
        </input>
      </header>
      <div
        id="addItem"
        onClick={() => AddItemOpen(open)}
      >
        <FontAwesomeIcon
          id="plusIcon"
          className="plusIcon"
          icon={faPlus}
        />
      </div>
      <div id="addItem_main">
      </div>
      <div id="main">
        <div id="leftMain">
          <div id="constraints">
          </div>
          <div id="restaurants_container">

            <div className="restaurant_main" onClick={() => rightSideShow(rightSide)}>
              <img className="restaurant_img" src="#"></img>
              <div className="restaurant_details">
                <div className="restaurant_row1">
                  <div className="restaurant_title">{dunderbaks[0].restaurant}</div>
                  <div className="restaurant_rating">{dunderbaks[0].rating}</div>
                </div>
                <div className="restaurant_row2">
                  <div className="restaurant_location"><i>{dunderbaks[0].city},{dunderbaks[0].state}</i></div>
                  <div className="restaurant_description">{dunderbaks[0].description}</div>
                </div>
              </div>
            </div>

            <div className="restaurant_main">
              <img className="restaurant_img" src="#"></img>
              <div className="restaurant_details">
                <div className="restaurant_row1">
                  <div className="restaurant_title">Portillos</div>
                  <div className="restaurant_rating">4.6</div>
                </div>
                <div className="restaurant_row2">
                  <div className="restaurant_location"><i>Tampa, FL</i></div>
                  <div className="restaurant_description">Old school Chicago vibes with the best hot dogs.</div>
                </div>
              </div>
            </div>

            <div className="restaurant_main">
              <img className="restaurant_img" src="#"></img>
              <div className="restaurant_details">
                <div className="restaurant_row1">
                  <div className="restaurant_title">Simply Ph√≥</div>
                  <div className="restaurant_rating">5.0</div>
                </div>
                <div className="restaurant_row2">
                  <div className="restaurant_location"><i>Tampa, FL</i></div>
                  <div className="restaurant_description">Best. Soup. Ever.</div>
                </div>
              </div>
            </div>

          </div>
        </div>
        <div id="rightMain">
        </div>
      </div>
    </div>
  );
}

export default App;
